const emailRegex = /^[A-Za-z0-9_\.]+@[A-Za-z]+\.[A-Za-z]{2,3}$/;

export const validateRegisterForm = (formValues) => {
    if(emailRegex.test(formValues.email) === false) {return false}
    if(formValues.password !== formValues.passwordConfirm) {return false}
    return true;
};
