const allowedFileTypes =['image/jpeg', 'image/jpg', 'image/png']
const maxFileSize = 5242880;

export const validateFile = (file) => {
   
    if(allowedFileTypes.indexOf(file.type) === -1){
        return false;
    }
    if(file.size > maxFileSize){
        return false;
    }
    return true;
};
