import { useState } from 'react'

import {
    emailValidator,
    passwordValidator,
    confirmPasswordValidator,
    fileValidator,
    titleValidator
} from '../utils/validation/validator';

export const useValidateForm = () => {

    const [errors, setErrors] = useState({
        email: {
            error: '',
            isValid: true
        },
        password: {
            error: '',
            isValid: true
        },
        confirmPassword: {
            error: '',
            isValid: true
        },
        file: {
            error: '',
            isValid: true
        },
        title: {
            error: '',
            isValid: true
        }

    });

    const validateForm = (formValues) => {


        if (formValues.email) {
            let error = emailValidator(formValues.email);

            if (error) {
                console.log(error);
                setErrors(err => ({ ...err, [err.email.isValid]: false }))
            }
        }
        if (formValues.password) {
            let error = passwordValidator(formValues.password);
            if (error) {
                console.log(error);
                setErrors(err => ({ ...err, [password]: [isValid] = false, [err.password.error]: error }))
            }
        }
        if (formValues.confirmPassword) {
            console.log(error);
            let error = confirmPasswordValidator(formValues.password, formValues.confirmPassword);
            if (error) {
                setErrors(err => ({ ...err, [err.confirmPassword.isValid]: false }))
            }
        }
        if (formValues.file) {
            let error = fileValidator(formValues.file);
            if (error) {
                setErrors(err => ({ ...err, [err.file.isValid]: false }))
            }
        }
        if (formValues.title) {
            let error = titleValidator(formValues.title);
            if (error) {
                setErrors(err => ({ ...err, [err.title]: false }))
            }
        }
    }

    return {
        errors,
        validateForm
    };

};
