import { useState, useEffect } from 'react'

import { validateFile } from '../../utils/validateFile'

export const Upload = () => {
    const [tags, setTags] = useState([]);
    const [tagInput, setTagInput] = useState('');
    const [file, setFile] = useState(null);
    const [fileErrors, setFileErrors] = useState([]);
    const [preview, setPreview] = useState(null);
   

    useEffect(() => {
        if (file) {
            setPreview(URL.createObjectURL(file))
            console.log(preview);
        }
        return () => {
            URL.revokeObjectURL(preview);
            setPreview(null);
        };
    }, [file])

    function tagsHandler(e) {
        e.preventDefault();
        if (e.key === ' ' || e.key === 'Enter') {
            setTags(tags => [...tags, tagInput])
            setTagInput('');
        } else if (e.key === 'Backspace') {
            setTagInput(val => val.slice(0, val.length - 1))
        } else if (e.key.length === 1) {
            setTagInput(val => val + e.key)
        }
    }

    function handleFilePreview(e) {
        const previewFile = e.target.files[0];
        if (validateFile(previewFile) === false) {
            setFileErrors(['The chosen file type is not supported']);
            setFile(null);
        }
        else {
            setFileErrors([]);
            setFile(previewFile);
        }
    }

    function removeTag(index) {
        setTags(tags => tags.filter((x, i) => i !== index))
    }
   

    return (
        <>
        {fullscreen &&
        <div className='fullscreen'>
            <img src={preview} alt="" />
        </div>
        }
            <div className='row tm-mb-50 ml-5 mr-0'>
                <div className="col-lg-4 col-12 mt-5">
                    <h2 className="tm-text-primary mb-5">Upload picture</h2>
                    <form action="" method="POST" className="tm-contact-form mx-auto">
                        <div className="form-group">
                            <input type="text" name="title" className="form-control rounded-0" placeholder="Title" required />
                        </div>

                        <div className="form-group">
                            <div className="tags-input-container form-control rounded-0">
                                {tags.length > 0 ?
                                    tags.map((t, i) => (

                                        <div key={i} className="tag-item">
                                            <span className="text">{t}</span>
                                            <span onClick={() => removeTag(i)} className="close">&times;</span>
                                        </div>

                                    )) : 'Add tag in the field below'}
                            </div>
                        </div>
                        <div className="form-group">
                            <input value={tagInput} onKeyDown={tagsHandler} type="text" name="tags" className="form-control rounded-0" placeholder="Image Tags" required />
                        </div>
                        <div className="form-group">
                            <select className="form-control" id="contact-select" name="inquiry">
                                <option value="-">Subject</option>
                                <option value="sales">Sales &amp; Marketing</option>
                                <option value="creative">Creative Design</option>
                                <option value="uiux">UI / UX</option>
                            </select>
                        </div>

                        <div className='form-group'>
                            <input className='form-control rounded-0' type="file" onChange={handleFilePreview} />
                        </div>

                        <div className="form-group tm-text-right">
                            <button type="submit" className="btn btn-primary">Send</button>
                        </div>
                    </form>

                </div>
                <div className="col-lg-8 col-12 mt-5">
                    <h2 className="tm-text-primary mb-5">Image preview</h2>
                    <div className='img-preview-container' >
                        {fileErrors.length > 0
                            ? fileErrors.map(f => <p className='text-danger'>{f}</p>)
                            : file === null
                                ? <p>No image chosen yet.Choose an image... </p>
                                : <img className="img-preview" src={preview} alt="" />}

                    </div>
                </div>
            </div>
        </>
    );
}
