import {useState} from 'react'

export const Upload = () => {
    const [tags ,setTags] = useState([]);
    const [tagInput ,setTagInput] = useState('');

    function tagsHandler(e){
        e.preventDefault();
        if(e.key === ' ' || e.key === 'Enter'){
            setTagInput('');
        }
        setTagInput(val => val + e.key);
    }
    return (
        <div class="col-lg-4 col-12 mb-5">
            <h2 class="tm-text-primary mb-5">Upload picture</h2>
            <form id="contact-form" action="" method="POST" class="tm-contact-form mx-auto">
                <div class="form-group">
                    <input type="text" name="title" class="form-control rounded-0" placeholder="Title" required />
                </div>
                <div class="form-group">
                    <input value={tagInput} onKeyDown={tagsHandler} type="text" name="tags" class="form-control rounded-0" placeholder="Image Tags" required />
                </div>
                <div class="form-group">
                    <select class="form-control" id="contact-select" name="inquiry">
                        <option value="-">Subject</option>
                        <option value="sales">Sales &amp; Marketing</option>
                        <option value="creative">Creative Design</option>
                        <option value="uiux">UI / UX</option>
                    </select>
                </div>
                <div class="form-group">

                </div>

                <div class="form-group tm-text-right">
                    <button type="submit" class="btn btn-primary">Send</button>
                </div>
            </form>
        </div>
    );
}
