import { useState } from 'react';
import { useAuthForm } from '../../hooks/useAuthForm'
import { reigister } from '../services/userServise';
import { validateRegisterForm } from '../../utils/validation/validateRegisterForm';

export const Register = () => {
    const [errors,setErrors] = useState({});

    const { formValues,onFormChange} = useAuthForm({
        username:'',
        email:'',
        password:'',
        passwordConfirm:''
    });

    const onFormSubmit = async (e) => {
        e.preventDefault();
        let [result,errors] = validateRegisterForm(formValues);
        if(result === false){
            setErrors(errors);
        }
        console.log(errors);
        // if(valResult){
        //     let user = await reigister(formValues.email,formValues.password);
        //     console.log(user);
        // }
        
    };


    return (

        <div className="col-12 mb-5">
            <h2 className="text-center tm-text-primary mb-5">Contact Page</h2>
            <form onSubmit={onFormSubmit} id="contact-form" action="" method="POST" className="tm-contact-form mx-auto">
                <div className="form-group">
                    <input 
                    className="form-control rounded-0" 
                    value={formValues.username} 
                    type="text" 
                    name="username" 
                    placeholder="Username" 
                    required 
                    onChange={onFormChange} />
                    <span className='text-danger'>{errors.username}</span>
                </div>
                <div className="form-group">
                    <input 
                    className="form-control rounded-0" 
                    value={formValues.email} 
                    type="email" name="email" 
                    placeholder="Email" 
                    required 
                    onChange={onFormChange} />
                    <span className='text-danger'>{errors.email}</span>
                </div>

                <div className="form-group">
                    <input 
                    className="form-control rounded-0" 
                    value={formValues.password} 
                    type="password" 
                    name="password" 
                    placeholder="Password" 
                    required 
                    onChange={onFormChange} />
                    <span className='text-danger'>{errors.password}</span>
                </div>
                <div className="form-group">
                    <input 
                    className="form-control rounded-0" 
                    value={formValues.passwordConfirm} 
                    type="password" 
                    name="passwordConfirm" 
                    placeholder="Confirm Password" 
                    required 
                    onChange={onFormChange} />
                </div>

                <div className="form-group tm-text-right">
                    <button type="submit" className="btn btn-primary">Register</button>
                </div>
            </form>
        </div>
    );
}
